// react
import React, { Component } from "react";

// third-party
import classNames from "classnames";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

// application
import AsyncAction from "./AsyncAction";
import Currency from "./Currency";
import InputNumber from "./InputNumber";
import ProductGallery from "./ProductGallery";
import Rating from "./Rating";
import { cartAddItem } from "../../store/cart";
import { compareAddItem } from "../../store/compare";
import { Wishlist16Svg, Compare16Svg } from "../../svg";
import { wishlistAddItem } from "../../store/wishlist";

class Product extends Component {
  constructor(props) {
    super(props);

    this.state = {
      quantity: 1,
    };
  }

  handleChangeQuantity = (quantity) => {
    this.setState({ quantity });
  };

  render() {
    const { product, layout, wishlistAddItem, compareAddItem, cartAddItem } =
      this.props;
    const { quantity } = this.state;
    let prices;

    if (product.compareAtPrice) {
      prices = (
        <React.Fragment>
          <span className="product__new-price">
            <Currency value={product.price} />
          </span>{" "}
          <span className="product__old-price">
            <Currency value={product.compareAtPrice} />
          </span>
        </React.Fragment>
      );
    } else {
      prices = <Currency value={product.price} />;
    }

    return (
      <div className={`product product--layout--${layout}`}>
        <div className="product__content">
          <ProductGallery layout={layout} images={product.images} />

          <div className="product__info">
            <div className="product__wishlist-compare">
              <AsyncAction
                action={() => wishlistAddItem(product)}
                render={({ run, loading }) => (
                  <button
                    type="button"
                    data-toggle="tooltip"
                    data-placement="right"
                    title="Wishlist"
                    onClick={run}
                    className={classNames("btn btn-sm btn-light btn-svg-icon", {
                      "btn-loading": loading,
                    })}
                  >
                    <Wishlist16Svg />
                  </button>
                )}
              />
              <AsyncAction
                action={() => compareAddItem(product)}
                render={({ run, loading }) => (
                  <button
                    type="button"
                    data-toggle="tooltip"
                    data-placement="right"
                    title="Compare"
                    onClick={run}
                    className={classNames("btn btn-sm btn-light btn-svg-icon", {
                      "btn-loading": loading,
                    })}
                  >
                    <Compare16Svg />
                  </button>
                )}
              />
            </div>
            <h1 className="product__name">{product.name}</h1>
            <div className="product__rating">
              <div className="product__rating-stars">
                <Rating value={product.rating} />
              </div>
              <div className="product__rating-legend">
                <Link to="/">{`${product.reviews} Reviews`}</Link>
                <span>/</span>
                <Link to="/">Пікір Жазу</Link>
              </div>
            </div>
            <div className="product__description">
              Бұл өнім пайдаланушылардың әртүрлі қолдану салаларындағы
              қажеттіліктерін қанағаттандыруға арналған әмбебап құрал болып
              табылады. Инновациялық дизайнымен және жоғары сапалы
              материалдарымен бұл өнім керемет өнімділік пен беріктікті
              қамтамасыз етеді.
            </div>
            <ul className="product__features">
              <li>Жылдамдық: 750 RPM</li>
              <li>Қуат Көзі: Сымсыз-Электр</li>
              <li>Қуат Элемент Түрі: Литий</li>
              <li>Кернеу: 20 Вольт</li>
              <li>Батарея Сыйымы: 2 Ah</li>
            </ul>
            <ul className="product__meta">
              <li className="product__meta-availability">
                Қол жетімділік: <span className="text-success">Қолда Бар</span>
              </li>
              <li>
                Brand:
                <Link to="/">Wakita</Link>
              </li>
              <li>SKU: 83690/32</li>
            </ul>
          </div>

          <div className="product__sidebar">
            <div className="product__availability">
              Қол жетімділік: <span className="text-success">Қолда Бар</span>
            </div>

            <div className="product__prices">{prices}</div>

            <form className="product__options">
              <div className="form-group product__option">
                <div className="product__option-label">Түс</div>
                <div className="input-radio-color">
                  <div className="input-radio-color__list">
                    <label
                      className="input-radio-color__item input-radio-color__item--white"
                      style={{ color: "#fff" }}
                      data-toggle="tooltip"
                      title="White"
                    >
                      <input type="radio" name="color" />
                      <span />
                    </label>
                    <label
                      className="input-radio-color__item"
                      style={{ color: "#ffd333" }}
                      data-toggle="tooltip"
                      title="Yellow"
                    >
                      <input type="radio" name="color" />
                      <span />
                    </label>
                    <label
                      className="input-radio-color__item"
                      style={{ color: "#ff4040" }}
                      data-toggle="tooltip"
                      title="Red"
                    >
                      <input type="radio" name="color" />
                      <span />
                    </label>
                    <label
                      className="input-radio-color__item input-radio-color__item--disabled"
                      style={{ color: "#4080ff" }}
                      data-toggle="tooltip"
                      title="Blue"
                    >
                      <input type="radio" name="color" disabled />
                      <span />
                    </label>
                  </div>
                </div>
              </div>
              <div className="form-group product__option">
                <div className="product__option-label">Материал</div>
                <div className="input-radio-label">
                  <div className="input-radio-label__list">
                    <label>
                      <input type="radio" name="material" />
                      <span>Темір</span>
                    </label>
                    <label>
                      <input type="radio" name="material" />
                      <span>Ағаш</span>
                    </label>
                    <label>
                      <input type="radio" name="material" disabled />
                      <span>Пластик</span>
                    </label>
                  </div>
                </div>
              </div>
              <div className="form-group product__option">
                <label
                  htmlFor="product-quantity"
                  className="product__option-label"
                >
                  Саны
                </label>
                <div className="product__actions">
                  <div className="product__actions-item">
                    <InputNumber
                      id="product-quantity"
                      aria-label="Quantity"
                      className="product__quantity"
                      size="lg"
                      min={1}
                      value={quantity}
                      onChange={this.handleChangeQuantity}
                    />
                  </div>
                  <div className="product__actions-item product__actions-item--addtocart">
                    <AsyncAction
                      action={() => cartAddItem(product, [], quantity)}
                      render={({ run, loading }) => (
                        <button
                          type="button"
                          onClick={run}
                          className={classNames("btn btn-primary btn-lg", {
                            "btn-loading": loading,
                          })}
                        >
                          Себетке қосу
                        </button>
                      )}
                    />
                  </div>
                  <div className="product__actions-item product__actions-item--wishlist">
                    <AsyncAction
                      action={() => wishlistAddItem(product)}
                      render={({ run, loading }) => (
                        <button
                          type="button"
                          data-toggle="tooltip"
                          title="Wishlist"
                          onClick={run}
                          className={classNames(
                            "btn btn-secondary btn-svg-icon btn-lg",
                            {
                              "btn-loading": loading,
                            }
                          )}
                        >
                          <Wishlist16Svg />
                        </button>
                      )}
                    />
                  </div>
                  <div className="product__actions-item product__actions-item--compare">
                    <AsyncAction
                      action={() => compareAddItem(product)}
                      render={({ run, loading }) => (
                        <button
                          type="button"
                          data-toggle="tooltip"
                          title="Compare"
                          onClick={run}
                          className={classNames(
                            "btn btn-secondary btn-svg-icon btn-lg",
                            {
                              "btn-loading": loading,
                            }
                          )}
                        >
                          <Compare16Svg />
                        </button>
                      )}
                    />
                  </div>
                </div>
              </div>
            </form>
          </div>

          <div className="product__footer">
            <div className="product__tags tags">
              <div className="tags__list">
                <Link to="/">Монтаждау</Link>
                <Link to="/">Электродтар</Link>
                <Link to="/">Шынжырлы аралар</Link>
              </div>
            </div>

            <div className="product__share-links share-links">
              <ul className="share-links__list">
                <li className="share-links__item share-links__item--type--like">
                  <Link to="/">Ұнайды</Link>
                </li>
                <li className="share-links__item share-links__item--type--tweet">
                  <Link to="/">Tweet</Link>
                </li>
                <li className="share-links__item share-links__item--type--pin">
                  <Link to="/">Бекіту</Link>
                </li>
                <li className="share-links__item share-links__item--type--counter">
                  <Link to="/">4K</Link>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

Product.propTypes = {
  /** product object */
  product: PropTypes.object.isRequired,
  /** one of ['standard', 'sidebar', 'columnar', 'quickview'] (default: 'standard') */
  layout: PropTypes.oneOf(["standard", "sidebar", "columnar", "quickview"]),
};

Product.defaultProps = {
  layout: "standard",
};

const mapDispatchToProps = {
  cartAddItem,
  wishlistAddItem,
  compareAddItem,
};

export default connect(() => ({}), mapDispatchToProps)(Product);
